mapper.insert:

insert into tbl_customer (id, owner, name, website,
  phone, create_by, create_time,
  edit_by, edit_time, contact_summary,
  next_contact_time, description, address
  )
values (#{id,jdbcType=CHAR}, #{owner,jdbcType=CHAR}, #{name,jdbcType=VARCHAR}, #{website,jdbcType=VARCHAR},
  #{phone,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, #{createTime,jdbcType=CHAR},
  #{editBy,jdbcType=VARCHAR}, #{editTime,jdbcType=CHAR}, #{contactSummary,jdbcType=VARCHAR},
  #{nextContactTime,jdbcType=CHAR}, #{description,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}
  )

mapper.select:
select c.id,u.name as owner,c.name,c.website,c.phone,u2.name as create_by,c.create_time, u3.name as edit_by,c.edit_time,c.contact_summary,c.next_contact_time,c.description, c.address from tbl_customer c join tbl_user u on c.owner=u.id join tbl_user u2 on c.create_by=u2.id left join tbl_user u3 on c.edit_by=u3.name
c.name like concat('%',#{name},'%')
u.name like concat('%',#{owner},'%')
c.phone=#{phone}
c.website like concat('%',#{website},'%')
order by c.create_time desc limit #{begin},#{pageSize}

mapper.delete:
delete from tbl_customer
where id in

  #{id}
