mapper.select:
select c.id,u1.name as owner,c.company,c.fullname,dv3.value as appellation, c.job,c.email,c.phone,c.website,c.mphone,dv1.value as source,dv2.value as state, u2.name as create_by,c.create_time,c.edit_time,u3.name as edit_by, c.description,c.contact_summary,c.next_contact_time,c.address from tbl_clue c join tbl_user u1 on c.owner=u1.id join tbl_user u2 on c.create_by=u2.id left join tbl_user u3 on c.edit_by=u3.id left join tbl_dic_value dv1 on c.source=dv1.id left join tbl_dic_value dv2 on c.state=dv2.id left join tbl_dic_value dv3 on c.appellation=dv3.id
and c.fullname like concat('%',#{fullname},'%')
and c.company like concat('%',#{company},'%')
and c.mphone like concat('%',#{mphone},'%')
and dv1.id = #{source}
and u1.name like concat('%',#{owner},'%')
and c.phone like concat('%',#{phone},'%')
and dv2.id = #{state}
order by c.create_time desc limit #{begin},#{pageSize}

mapper.update:
update tbl_clue set
  owner=#{owner},company=#{company},fullname=#{fullname},appellation=#{appellation},job=#{job},email=#{email},
  phone=#{phone},website=#{website},mphone=#{mphone},state=#{state},source=#{source},edit_time=#{editTime},
  edit_by=#{editBy},description=#{description},contact_summary=#{contactSummary},next_contact_time=#{nextContactTime},
  address=#{address}
where id=#{id}
